using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataTotem{		/**		* ID		*/		public int Id;		/**		* 合成物品ID		*/		public int ItemID;		/**		* 主物品ID		*/		public int NeeditemID;		/**		* 合成类型		*/		public int Type;		/**		* 合成等级		*/		public int Level;		/**		* 合成所需属性ID		*/		public int BaseID;		/**		* 合成所需属性值		*/		public int Needbase;		/**		* 所需人物等级		*/		public int HumanLevel;		/**		* 描述		*/		public string Desc;		static public Dictionary<int, TbDataTotem> temples=new Dictionary<int,TbDataTotem>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataTotem tp=new TbDataTotem();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.ItemID=(int)tb["ItemID"];				tp.NeeditemID=(int)tb["NeeditemID"];				tp.Type=(int)tb["Type"];				tp.Level=(int)tb["Level"];				tp.BaseID=(int)tb["BaseID"];				tp.Needbase=(int)tb["Needbase"];				tp.HumanLevel=(int)tb["HumanLevel"];				tp.Desc=(string)tb["Desc"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataTotem select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}