using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataTotemBuffer{		/**		* ID		*/		public int Id;		/**		* 图标		*/		public string Icon;		/**		* 名称		*/		public string Name;		static public Dictionary<int, TbDataTotemBuffer> temples=new Dictionary<int,TbDataTotemBuffer>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataTotemBuffer tp=new TbDataTotemBuffer();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Icon=(string)tb["Icon"];				tp.Name=(string)tb["Name"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataTotemBuffer select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}