using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataTreasure{		/**		* ID		*/		public int Id;		/**		* 放入数量		*/		public int InNum;		/**		* 最小取出数目		*/		public int OutMinNum;		/**		* 最大取出数目		*/		public int OutMaxNum;		static public Dictionary<int, TbDataTreasure> temples=new Dictionary<int,TbDataTreasure>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataTreasure tp=new TbDataTreasure();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.InNum=(int)tb["InNum"];				tp.OutMinNum=(int)tb["OutMinNum"];				tp.OutMaxNum=(int)tb["OutMaxNum"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataTreasure select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}