using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataTreasureChest{		/**		* ID		*/		public int Id;		/**		* 宝箱ID		*/		public int BoxID;		/**		* 物品ID		*/		public int[] ItemID;		/**		* 物品数		*/		public int[] Itemnum;		/**		* 权重		*/		public int[] Weight;		static public Dictionary<int, TbDataTreasureChest> temples=new Dictionary<int,TbDataTreasureChest>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataTreasureChest tp=new TbDataTreasureChest();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.BoxID=(int)tb["BoxID"];				tp.ItemID=(int[])tb["ItemID"];				tp.Itemnum=(int[])tb["Itemnum"];				tp.Weight=(int[])tb["Weight"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataTreasureChest select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}